import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { Typography, CircularProgress, Grid, Box, ListItem, ListItemText, List } from '@mui/material';
import { styled } from '@mui/system';

const Image = styled('img')({
    maxWidth: '100%', // Limit the image width to its container
    maxHeight: '100%', // Limit the image height to its container
    // borderRadius: '50%',
    margin: 'auto',
    display: 'block',
    transition: 'transform 0.3s ease-in-out', // Add transition for smooth animation
    position: 'relative',
    top: '50%',
    transform: 'translateY(-50%)', // Center the image vertically
});

const CenteredBox = styled(Box)({
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    height: '100%',
});

const PostDetailsPage: React.FC = () => {
    const { id } = useParams<{ id: string }>();
    const [pokemonDetails, setPokemonDetails] = useState<any>(null);
    const [loading, setLoading] = useState<boolean>(true);

    useEffect(() => {
        const fetchPokemonDetails = async () => {
            try {
                const response = await fetch(`https://pokeapi.co/api/v2/pokemon/${id}`);
                const data = await response.json();
                setPokemonDetails(data);
                setLoading(false);
            } catch (error) {
                console.error('Error fetching Pokémon details:', error);
                setLoading(false);
            }
        };

        fetchPokemonDetails();
    }, [id]);

    if (loading) {
        return <CircularProgress />;
    }

    if (!pokemonDetails) {
        return <Typography variant="body1">Pokémon not found</Typography>;
    }

    const { name, types, stats } = pokemonDetails;

    return (
        <Grid container spacing={2}>
            <Grid item xs={12} sm={6}>
                <CenteredBox style={{ padding: '20px', backgroundColor: '#f0f0f0', borderRadius: '8px' }}>
                    <Typography variant="h3">Pokémon Details</Typography>
                    <Typography variant="body1">ID: {id}</Typography>
                    <Typography variant="body1">Name: {name}</Typography>
                    <Typography variant="body1">Type: {types.map((type: any) => type.type.name).join(', ')}</Typography>
                    <Typography variant="body1">Stats:</Typography>
                    <List>
                        {stats.map((stat: any) => (
                            <ListItem key={stat.stat.name}>
                                <ListItemText primary={`${stat.stat.name}: ${stat.base_stat}`} />
                            </ListItem>
                        ))}
                    </List>
                    {/* Preview of evolution can be added here */}
                </CenteredBox>

            </Grid>
            <Grid item xs={12} sm={6}>
                <Image
                    src={`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/other/dream-world/${id}.svg`}
                    alt={name}
                    style={{ position: 'relative', top: '50%', transform: 'translateY(-50%)' }} // Center the image vertically
                />
            </Grid>
        </Grid>
    );
};

export default PostDetailsPage;
