import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { Typography, CircularProgress, Grid, Box, ListItem, ListItemText, List } from '@mui/material';
import { styled } from '@mui/system';

const Image = styled('img')({
    height: 400, // Adjust the height as needed for a larger size
    width: 'auto', // Let the width adjust automatically to maintain aspect ratio
    borderRadius: '50%',
    margin: 'auto',
    display: 'block',
  });

const PostDetailsPage: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const [pokemonDetails, setPokemonDetails] = useState<any>(null);
  const [loading, setLoading] = useState<boolean>(true);

  useEffect(() => {
    const fetchPokemonDetails = async () => {
      try {
        const response = await fetch(`https://pokeapi.co/api/v2/pokemon/${id}`);
        const data = await response.json();
        setPokemonDetails(data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching Pokémon details:', error);
        setLoading(false);
      }
    };

    fetchPokemonDetails();
  }, [id]);

  if (loading) {
    return <CircularProgress />;
  }

  if (!pokemonDetails) {
    return <Typography variant="body1">Pokémon not found</Typography>;
  }

  const { name, sprites, types, stats } = pokemonDetails;

  return (
    <Grid container spacing={2}>
      <Grid item xs={12} sm={6}>
        <Box textAlign="left">
          <Typography variant="h2">Pokémon Details</Typography>
          <Typography variant="body1">ID: {id}</Typography>
          <Typography variant="body1">Name: {name}</Typography>
          <Typography variant="body1">Type: {types.map((type: any) => type.type.name).join(', ')}</Typography>
          <Typography variant="body1">Stats:</Typography>
          <List>
            {stats.map((stat: any) => (
              <ListItem key={stat.stat.name}>
                <ListItemText primary={`${stat.stat.name}: ${stat.base_stat}`} />
              </ListItem>
            ))}
          </List>
          {/* Preview of evolution can be added here */}
        </Box>
      </Grid>
      <Grid item xs={12} sm={6}>
        <Image src={sprites.front_default} alt={name} />
      </Grid>
    </Grid>
  );
};

export default PostDetailsPage;
