import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { Typography, CircularProgress, Grid, Box } from '@mui/material';
import { styled } from '@mui/system';

const Image = styled('img')({
  height: 200,
  width: 200,
  borderRadius: '80%',
  margin: 'auto',
  display: 'block',
});

const PostDetailsPage: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const [pokemonDetails, setPokemonDetails] = useState<any>(null);
  const [loading, setLoading] = useState<boolean>(true);

  useEffect(() => {
    const fetchPokemonDetails = async () => {
      try {
        const response = await fetch(`https://pokeapi.co/api/v2/pokemon/${id}`);
        const data = await response.json();
        setPokemonDetails(data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching Pokémon details:', error);
        setLoading(false);
      }
    };

    fetchPokemonDetails();
  }, [id]);

  if (loading) {
    return <CircularProgress />;
  }

  if (!pokemonDetails) {
    return <Typography variant="body1">Pokémon not found</Typography>;
  }

  return (
    <Grid container spacing={2}>
      <Grid item xs={12} sm={6}>
        <Box textAlign="left">
          <Typography variant="h2">Pokémon Details</Typography>
          <Typography variant="body1">ID: {id}</Typography>
          <Typography variant="body1">Name: {pokemonDetails.name}</Typography>
        </Box>
      </Grid>
      <Grid item xs={12} sm={6}>
        <Image src={pokemonDetails.sprites.front_default} alt={pokemonDetails.name} />
      </Grid>
    </Grid>
  );
};

export default PostDetailsPage;
