import React, { useState, useEffect } from 'react';
import { useParams, useHistory } from 'react-router-dom';
import { Typography, CircularProgress, Grid, Box, ListItem, ListItemText, List, Link } from '@mui/material';
import { styled } from '@mui/system';

const Image = styled('img')({
  height: 200,
  width: 200,
  borderRadius: '50%',
  margin: 'auto',
  display: 'block',
});

const PostDetailsPage: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const [pokemonDetails, setPokemonDetails] = useState<any>(null);
  const [loading, setLoading] = useState<boolean>(true);
  const history = useHistory();

  useEffect(() => {
    const fetchPokemonDetails = async () => {
      try {
        const response = await fetch(`https://pokeapi.co/api/v2/pokemon/${id}`);
        const data = await response.json();
        setPokemonDetails(data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching Pokémon details:', error);
        setLoading(false);
      }
    };

    fetchPokemonDetails();
  }, [id]);

  const handleEvolutionClick = (evolutionId: number) => {
    history.push(`/post/${evolutionId}`);
  };

  if (loading) {
    return <CircularProgress />;
  }

  if (!pokemonDetails) {
    return <Typography variant="body1">Pokémon not found</Typography>;
  }

  const { name, sprites, types, stats, species } = pokemonDetails;

  // Extract evolution chain information from the species data
  const evolutionChainUrl = species.url; // Assuming species endpoint provides evolution chain URL

  return (
    <Grid container spacing={2}>
      <Grid item xs={12} sm={6}>
        <Box textAlign="left">
          <Typography variant="h2">Pokémon Details</Typography>
          <Typography variant="body1">ID: {id}</Typography>
          <Typography variant="body1">Name: {name}</Typography>
          <Typography variant="body1">Type: {types.map((type: any) => type.type.name).join(', ')}</Typography>
          <Typography variant="body1">Stats:</Typography>
          <List>
            {stats.map((stat: any) => (
              <ListItem key={stat.stat.name}>
                <ListItemText primary={`${stat.stat.name}: ${stat.base_stat}`} />
              </ListItem>
            ))}
          </List>
          {species.evolves_from_species && (
            <Typography variant="body1">Evolves from: {species.evolves_from_species.name}</Typography>
          )}
          {/* Evolution chain previews */}
          <Typography variant="body1">Evolutions:</Typography>
          <List>
            {evolutionChain.map((evolution: any) => (
              <ListItem key={evolution.id} button onClick={() => handleEvolutionClick(evolution.id)}>
                <ListItemText primary={evolution.name} />
              </ListItem>
            ))}
          </List>
        </Box>
      </Grid>
      <Grid item xs={12} sm={6}>
        <Image src={sprites.front_default} alt={name} />
      </Grid>
    </Grid>
  );
};

export default PostDetailsPage;
